        % Value changed function: LivewaveformplotSwitch
        function LivewaveformplotSwitchValueChanged(app, event)
            value = app.LivewaveformplotSwitch.Value;


            switch value
                case 'On'
                    app.LiveLamp.Color = 'green';

                    i = 1;
                    app.matr = zeros(1,7);
                    app.matr1 = zeros(1,7);

                    Plotgraph=plot(app.UIAxes,app.matr(:,1),app.matr(:,2));
                    t(i) = 0;
                    tic

                    while ishandle(app.UIAxes)

                        string = readline(app.s);
                        string=convertStringsToChars(string);
                        %   string = read(s,50,'char')
                        if(length(string)~=1)
                            a =split(string,[" ",'/t']);
                            j = 1;
                            v = zeros(1,7);
                            for k=1:length(a)
                                %if(~isempty(a{k}))
                                if(~isnan(str2double(a{k})))
                                    %           z = a{k};
                                    %           z = split(z,'-');
                                    %           v(j) = str2double(z);
                                    %        end
                                    v(j) = str2double(a{k});
                                    j = j+1;
%                                 else
%                                     v = v(1,1:1:length(v)-1);
                                end

                            end
                            x = v;

                            if(size(x,2)==7)
                                app.matr = [app.matr;x]
                            end

                        end

                        i = i + 1;
                        t(i) = toc;
                        minlen(i) = min(length(t), length(app.matr));



                        if app.value1 == 1
                            Plotgraph1 = Plotgraph;
                            set(Plotgraph1,'XData',t(1:minlen(i)),'YData',app.matr(1:minlen(i),1))
                            %hold on
                            %grid(app.UIAxes)
                            legend(app.UIAxes,'Time comparison')
                            drawnow
                            pause(0.0004)

                        elseif app.value2 == 1
                            Plotgraph2 = Plotgraph;
                            set(Plotgraph2,'XData',t(1:minlen(i)),'YData',app.matr(:,2))
                            %hold on
                            %grid(app.UIAxes)
                            legend(app.UIAxes,'Roll Angular Velocity - Ox Axis')
                            drawnow
                            pause(0.0004)


                        elseif app.value3 == 1
                            Plotgraph3 = Plotgraph;
                            set(Plotgraph3,'XData',t(1:minlen(i)),'YData',app.matr(:,3))
                            %hold on
                            %grid(app.UIAxes)
                            legend(app.UIAxes,'Pitch Angular Velocity - Oy Axis')
                            drawnow
                            pause(0.0004)

                        elseif app.value4 == 1
                            Plotgraph4 = Plotgraph;
                            set(Plotgraph4,'XData',t(1:minlen(i)),'YData',app.matr(:,4))
                            %hold on
                            %grid(app.UIAxes)
                            legend(app.UIAxes,'Yaw Angular Velocity - Oz Axis')
                            drawnow
                            pause(0.0004)

                        elseif app.value5 == 1
                            Plotgraph5= Plotgraph;
                            set(Plotgraph5,'XData',t(1:minlen(i)),'YData',app.matr(:,5))
                            %hold on
                            %grid(app.UIAXes)
                            legend(app.UIAxes,'Roll Position - Ox Axis')
                            drawnow
                            pause(0.0004)

                        elseif app.value6 == 1
                            Plotgraph6 = Plotgraph;
                            set(Plotgraph6,'XData',t(1:minlen(i)),'YData',app.matr(:,6))
                            %hold on
                            %grid(app.UIAxes)
                            legend(app.UIAxes,'Pitch Position - Oy Axis')
                            drawnow
                            pause(0.0004)

                        end

                        if app.voltageChangingValue == 1
                            app.matr(:,2) = app.matr(:,2);
                            app.matr(:,3) = app.matr(:,3);
                            app.matr(:,4) = app.matr(:,4);
                            app.matr(:,5) = app.matr(:,5);
                            app.matr(:,6) = app.matr(:,6);
                        end

                        if app.voltageChangingValue == 10
                            app.matr(:,2) = 0.1*app.matr(:,2);
                            app.matr(:,3) = 0.1*app.matr(:,3);
                            app.matr(:,4) = 0.1*app.matr(:,4);
                            app.matr(:,5) = 0.1*app.matr(:,5);
                            app.matr(:,6) = 0.1*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 20
                            app.matr(:,2) = 0.2*app.matr(:,2);
                            app.matr(:,3) = 0.2*app.matr(:,3);
                            app.matr(:,4) = 0.2*app.matr(:,4);
                            app.matr(:,5) = 0.2*app.matr(:,5);
                            app.matr(:,6) = 0.2*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 30
                            app.matr(:,2) = 0.3*app.matr(:,2);
                            app.matr(:,3) = 0.3*app.matr(:,3);
                            app.matr(:,4) = 0.3*app.matr(:,4);
                            app.matr(:,5) = 0.3*app.matr(:,5);
                            app.matr(:,6) = 0.3*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 40
                            app.matr(:,2) = 0.4*app.matr(:,2);
                            app.matr(:,3) = 0.4*app.matr(:,3);
                            app.matr(:,4) = 0.4*app.matr(:,4);
                            app.matr(:,5) = 0.4*app.matr(:,5);
                            app.matr(:,6) = 0.4*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 50
                            app.matr(:,2) = 0.5*app.matr(:,2);
                            app.matr(:,3) = 0.5*app.matr(:,3);
                            app.matr(:,4) = 0.5*app.matr(:,4);
                            app.matr(:,5) = 0.5*app.matr(:,5);
                            app.matr(:,6) = 0.5*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 60
                            app.matr(:,2) = 0.6*app.matr(:,2);
                            app.matr(:,3) = 0.6*app.matr(:,3);
                            app.matr(:,4) = 0.6*app.matr(:,4);
                            app.matr(:,5) = 0.6*app.matr(:,5);
                            app.matr(:,6) = 0.6*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 70
                            app.matr(:,2) = 0.7*app.matr(:,2);
                            app.matr(:,3) = 0.7*app.matr(:,3);
                            app.matr(:,4) = 0.7*app.matr(:,4);
                            app.matr(:,5) = 0.7*app.matr(:,5);
                            app.matr(:,6) = 0.7*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 80
                            app.matr(:,2) = 0.8*app.matr(:,2);
                            app.matr(:,3) = 0.8*app.matr(:,3);
                            app.matr(:,4) = 0.8*app.matr(:,4);
                            app.matr(:,5) = 0.8*app.matr(:,5);
                            app.matr(:,6) = 0.8*app.matr(:,6);
                        end

                        if app.voltageChangingValue == 90
                            app.matr(:,2) = 0.9*app.matr(:,2);
                            app.matr(:,3) = 0.9*app.matr(:,3);
                            app.matr(:,4) = 0.9*app.matr(:,4);
                            app.matr(:,5) = 0.9*app.matr(:,5);
                            app.matr(:,6) = 0.9*app.matr(:,6);
                        end

                        if app.timeChangingValue == 1
                            app.matr(:,1) = 0.1*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.2
                            app.matr(:,1) = 0.2*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.3
                            app.matr(:,1) = 0.3*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.4
                            app.matr(:,1) = 0.1*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.5
                            app.matr(:,1) = 0.5*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.6
                            app.matr(:,1) = 0.6*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.7
                            app.matr(:,1) = 0.7*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.8
                            app.matr(:,1) = 0.8*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.9
                            app.matr(:,1) = 0.9*app.matr(:,1);
                        end

                        if app.timeChangingValue == 0.1
                            app.matr(:,1) = app.matr(:,1);
                        end

                        app.matr1 = app.matr;
                    end

                case 'Off'
                    app.LiveLamp.Color = 'red';
                    delete(app.s)
                otherwise
                    app.LiveLampColor = 'red';
            end


        end

        % Callback function
        function StopSerialCOMButtonPushed(app, event)
            clear
        end

        % Callback function: Menu_12, StopSerialCOMButton
        function StartSerialCOMButtonValueChanged(app, event)
