        % Value changed function: StartEditField_2
        function StartEditField_2ValueChanged(app, event)
            app.PlotIntervalValue = app.StartEditField_2.Value;
            app.PlotIntervalValue = str2double(app.PlotIntervalValue);
        end

        % Button pushed function: PlotintervalButton
        function PlotintervalButtonPushed(app, event)
            app.matr = app.matr(app.PlotIntervalValue:end,:);
        end

        % Button pushed function: QuickStartButton
        function QuickStartButtonPushed(app, event)
            app.s = serialport("COM6",9600);
            %app.s.BaudRate = 115200;

            set(app.s,'Parity','none');
            set(app.s,'DataBits',8);
            set(app.s,'StopBit',1);
            configureTerminator(app.s,"LF");

        end

        % Button pushed function: AutosetButton
        function AutosetButtonPushed(app, event)
           app.autoSet = sgolayfilt(app.matr(:,:),3,15);
           app.matr = app.autoSet;
        end
    end

