        % Create UIFigure and components
        function createComponents(app)

            % Create UIFigure and hide until all components are created
            app.UIFigure = uifigure('Visible', 'off');
            app.UIFigure.Position = [100 100 893 759];
            app.UIFigure.Name = 'MATLAB App';

            % Create Menu
            app.Menu = uimenu(app.UIFigure);
            app.Menu.Text = 'Menu';

            % Create SelectStopBitsMenu
            app.SelectStopBitsMenu = uimenu(app.Menu);
            app.SelectStopBitsMenu.Text = 'Select StopBits';

            % Create Menu_21
            app.Menu_21 = uimenu(app.SelectStopBitsMenu);
            app.Menu_21.MenuSelectedFcn = createCallbackFcn(app, @Menu_21Selected, true);
            app.Menu_21.Text = '1';

            % Create Menu_22
